mapper.select:
		
	  select a.PROCESSDEFINITION_KEY,PROCESS_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME,TERMINATION_COUNT  
	  from      
      ((select PROCESSDEFINITION_KEY,PROCESS_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT,sum(WORK_TIME) as SUM_TIME 
	      from FIXBPA_FLOW_PROCESSINSTANCE 
		  where 1=1
		  	  
	    		and PROCESS_NAME like '%${procName}%' 
			
			  
	    		and START_TIME >= #{dateStart}
			
			  
				and START_TIME <= #{dateEnd}
			
	      group by PROCESSDEFINITION_KEY,PROCESS_NAME) a 
  
      left join
      
      (select PROCESSDEFINITION_KEY,count(1) AS TERMINATION_COUNT
      from FIXBPA_FLOW_PROCESSINSTANCE 
      where INSTANCE_STATUS = 'TERMINATION'
          
	   		and PROCESS_NAME like '%${procName}%' 
		
		  
	   		and START_TIME >= #{dateStart}
		
		  
			and START_TIME <= #{dateEnd}
		
      group by PROCESSDEFINITION_KEY) b
      on a.PROCESSDEFINITION_KEY=b.PROCESSDEFINITION_KEY)
      
		  
			order by AVG_TIME ${timeOrder}
  		
  		  
			order by DONE_COUNT ${doneOrder}
  		
  		  
     		order by UNDONE_COUNT ${undoneOrder}
  		
  		  
     		order by ALL_COUNT ${allOrder}

select count(1) AS PAGE_SIZE from (select count(1) from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY,PROCESS_NAME) tb

select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME 
from (select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,sum(ALL_COUNT) as ALL_COUNT,sum(DONE_COUNT) as DONE_COUNT,sum (SUM_TIME) as SUM_TIME 
		from (select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1 
				  
	        		and PROCESSDEFINITION_KEY = #{procKey} 
	    		
	    		  
	        		and PROCESSDEFINITION_ID = #{procID} 
	    		
				group by PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME 
				union 
			   select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,0 as ALL_COUNT, 0 as DONE_COUNT, 0 as SUM_TIME
				from FIXBPA_FLOW_NODE
				where 1=1 
				  
	        		and PROCESSDEFINITION_KEY = #{procKey} 
	    		
	    		  
	        		and PROCESSDEFINITION_ID = #{procID} 
	    		 ) 
 				group by PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb2

		select * from 
			(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME from
				(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1
				  
			    	and PROCESSDEFINITION_KEY = #{procKey}
			    
			      
			    	and PROCESSDEFINITION_ID = #{procID} 
			    
			      
			    	and NODE_ID = #{nodeID}
			    
			      
        			and USER_NAME like '%${userName}%' 
	    		
				group by PROCESSDEFINITION_ID,USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb ) tb2 
				where 1=1
				  
			    	and AVG_TIME >= #{secSumStart} and DONE_COUNT <> -1
			    
			      
			    	and AVG_TIME <= #{secSumEnd} and DONE_COUNT <> -1
			    
			      
			    	and DONE_COUNT >= #{doneCountStart}
			    
			      
			    	and DONE_COUNT <= #{doneCountEnd}
			    
			      
			    	and UNDONE_COUNT >= #{undoneCountStart}
			    
			      
			    	and UNDONE_COUNT <= #{undoneCountEnd}
			    
			    
				  
        			order by AVG_TIME ${timeOrder}
	    		
	    		  
        			order by DONE_COUNT ${doneOrder}
	    		
	    		  
        			order by UNDONE_COUNT ${undoneOrder}
	    		
	    		  
        			order by ALL_COUNT ${allOrder}

		select count(1) from 
			(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME from 
				(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1
				  
			    	and PROCESSDEFINITION_KEY = #{procKey} 
			    
			      
			    	and PROCESSDEFINITION_ID = #{procID} 
			    
			      
			    	and NODE_ID = #{nodeID} 
			    
			      
        			and USER_NAME like '%${userName}%' 
	    		
				group by PROCESSDEFINITION_ID,USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb ) tb2 
				where 1=1
				  
			    	and AVG_TIME >= #{secSumStart} and DONE_COUNT <> -1
			    
			      
			    	and AVG_TIME <= #{secSumEnd} and DONE_COUNT <> -1
			    
			      
			    	and DONE_COUNT >= #{doneCountStart}
			    
			      
			    	and DONE_COUNT <= #{doneCountEnd}
			    
			      
			    	and UNDONE_COUNT >= #{undoneCountStart}
			    
			      
			    	and UNDONE_COUNT <= #{undoneCountEnd}

    	SELECT * FROM (SELECT PROCESSDEFINITION_KEY,USER_NAME,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) AS UNDONE_COUNT,CASE WHEN DONE_COUNT = 0 THEN -1 ELSE (SUM_TIME/DONE_COUNT) END AS AVG_TIME FROM 
        (SELECT PROCESSDEFINITION_KEY,USER_NAME,NODE_ID,NODE_NAME,COUNT(1) AS ALL_COUNT,COUNT(END_TIME) AS DONE_COUNT, SUM(WORK_TIME) AS SUM_TIME 
        FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
        WHERE 1=1
		
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) TB ) TB2 WHERE 1=1 AND USER_NAME IS NOT NULL
        
        	AND AVG_TIME >= 0
        
        ORDER BY ${orderColum} ${orderType}

    	SELECT  CASE WHEN DONE_COUNT = 0 THEN -1 ELSE (SUM_TIME/DONE_COUNT) END AS AVG_TIME,'平均处理时间' AS AVG_TIME_NAME FROM (SELECT PROCESSDEFINITION_KEY,COUNT(1) AS ALL_COUNT,COUNT(END_TIME) AS DONE_COUNT, SUM(WORK_TIME) AS SUM_TIME 
        FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
        WHERE 1=1
        
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY PROCESSDEFINITION_KEY)

    	SELECT SUM(DONE_COUNT)/COUNT(USER_NAME) AS AVG_DONE_COUNT, '平均完成次数' AS AVG_DONE_COUNT_NAME FROM (SELECT USER_NAME ,COUNT(END_TIME) DONE_COUNT
            FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
            WHERE 1=1 
        
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY USER_NAME)

select a.PROCESSDEFINITION_KEY,PROCESS_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME,TERMINATION_COUNT from ((select PROCESSDEFINITION_KEY,PROCESS_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT,sum(WORK_TIME) as SUM_TIME from FIXBPA_FLOW_PROCESSINSTANCE group by PROCESSDEFINITION_KEY,PROCESS_NAME) a
left join
(select PROCESSDEFINITION_KEY,count(1) AS TERMINATION_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'TERMINATION' group by PROCESSDEFINITION_KEY) b on a.PROCESSDEFINITION_KEY=b.PROCESSDEFINITION_KEY) ORDER BY ${orderColum} ${orderType}
